mapper.update:
UPDATE bw_zzy_users


        username=#{username}


        ,password=#{password}


        ,`name`=#{name}


        ,sex = #{sex}


        ,tel=#{tel}


        ,email = #{email}


        ,hiredate=#{hiredate}


        ,root=0


        ,dept_id = #{deptId}


        ,`role`=#{role}

    --             role是list的时候






        ,status=#{status}


WHERE id=#{id}

mapper.select:
select id
from ssm.bw_zzy_users
where username = #{username}
  and password = #{password}

SELECT DISTINCT u.id, u.name, u.sex, u.tel, u.email, u.hiredate, u.root, u.status, ( SELECT GROUP_CONCAT(role_name SEPARATOR ", ") FROM bw_zzy_role WHERE JSON_CONTAINS(u.role,CONVERT(id,CHAR)) ) AS roles, d.dept_name AS dept FROM bw_zzy_users u LEFT JOIN bw_zzy_role r ON JSON_CONTAINS(u.role,CONVERT(r.id,CHAR)) LEFT JOIN bw_zzy_dept d ON u.dept_id = d.id WHERE 1=1
AND u.name LIKE #{name}
AND u.sex=#{sex}
AND r.role_name=#{role}
AND d.id =#{deptId}
AND u.status=#{status}
LIMIT #{start},#{length}

SELECT COUNT(DISTINCT u.id)
FROM bw_zzy_users u
    LEFT JOIN bw_zzy_role r ON JSON_CONTAINS(u.role,CONVERT(r.id,CHAR))
    LEFT JOIN bw_zzy_dept d ON u.dept_id = d.id
WHERE 1=1

    AND u.name LIKE #{name}


    AND u.sex=#{sex}


    AND r.role_name=#{role}


    AND d.id =#{deptId}


    AND u.status=#{status}

mapper.insert:
        INSERT INTO bw_zzy_users


                username=#{username}


                ,password=#{password}


                ,`name`=#{name}


                ,sex = #{sex}


                ,tel=#{tel}


                ,email = #{email}


                ,hiredate=#{hiredate}


                ,root=0


                ,dept_id = #{deptId}


                ,`role`=#{role}

--             role是list的时候






                ,status=#{status}

mapper.delete:
DELETE  FROM bw_zzy_users
WHERE id IN

    #{id}
